mapper.select:


     and order_code like concat('%', #{orderCode}, '%')
     and customer_id = #{customerId}
     and supplier_id = #{supplierId}
     and order_date = #{orderDate}
     and delivery_date = #{deliveryDate}
     and order_status = #{orderStatus}
     and order_type = #{orderType}
     and salesman = #{salesman}

order by create_time desc

mapper.update:
update crm_order
order_code = #{orderCode}, customer_id = #{customerId}, supplier_id = #{supplierId}, order_date = #{orderDate}, supplier_ship_date = #{supplierShipDate}, supplier_arrival_date = #{supplierArrivalDate}, our_ship_date = #{ourShipDate}, customer_receive_date = #{customerReceiveDate}, delivery_date = #{deliveryDate}, order_status = #{orderStatus}, order_type = #{orderType}, urgency_level = #{urgencyLevel}, salesman = #{salesman}, total_amount = #{totalAmount}, profit = #{profit}, profit_rate = #{profitRate}, payment_method = #{paymentMethod}, logistics_method = #{logisticsMethod}, delivery_address = #{deliveryAddress}, special_requirements = #{specialRequirements}, update_by = #{updateBy}, update_time = #{updateTime}, remark = #{remark},
where id = #{id}
