mapper.select:


     and product_code like concat('%', #{productCode}, '%')
     and supplier like concat('%', #{supplier}, '%')
     and brand = #{brand}
     and status = #{status}
     and stock_date = #{stockDate}

order by stock_date desc

mapper.update:
update inv_stock
stock_date = #{stockDate}, product_code = #{productCode}, product_detail = #{productDetail}, price = #{price}, quantity = #{quantity}, delivery_time = #{deliveryTime}, supplier = #{supplier}, brand = #{brand}, remark = #{remark}, sheet_name = #{sheetName}, tags = #{tags}, status = #{status}, update_by = #{updateBy}, update_time = #{updateTime},
where id = #{id}
